import tt from 'trusted-types';

const noop = (i: any) => i
const rules = {
  createHTML: noop,
  createURL: (i) => false,
}

let appPolicy: any;
let withTrustedTypes = true

export const getPolicy = () => {
  if (withTrustedTypes && appPolicy === undefined) {
    appPolicy = tt.createPolicy('app-policy', rules)
  }

  if (withTrustedTypes) return appPolicy
  else return rules
}
